import React from 'react';


const Game =()=>{
const {name,img,comment}=React.createRef();
function handleSubmit(){
    console.log("submitted");
}
return(
<div>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          ref={name}
          /* call the ref with the 'ref' attribute */ required
        ></input>
        <br />
        <label>ImgUpload</label>{" "}
        <input type="text" ref={img} required></input>
        <br />
        <label>Comment:</label>
        <textarea rows="5" cols="90" ref={comment} required></textarea>
        <br />
        <button type="submit">
          <i className="fas fa-envelope-open-text"></i>
        </button>
        <br />
      </form>
    </div>
    )
}

export default Game;
