import React from 'react';


const Game =()=>{
const {name,img,comment}=React.createRef();

function handleSubmit(){
    console.log("submitted");
}
return(
<div>
      <form className="comment-form" onSubmit={handleSubmit}>
        <label>name</label>
        <input
          type="text"
          ref={name}
          /* call the ref with the 'ref' attribute */ required
        ></input>
        <br />
        <label>comment</label>
        <textarea rows="2" ref={comment} required></textarea>
        <br />
        <input type="file" ref={img} name="file" onChange={this.onChangeHandler}/>
        <button>upload your website image here <i className="fas fa-chevron-circle-up"></i></button>
        <br />
        <button type="submit">
          submit
        </button>
        <br />
      </form>
    </div>
    )
}

export default Game;
