import React,{useState} from 'react';
import axios from 'axios';

const Game =()=>{

  const {name,comment,file}=React.createRef();
  const [selectedFile, setFile] = useState({
    name:null,
    comment:null,
    file:null
  });


  function maxSelectFile(e){
    let files = e.target.files // create file object
    if (files.length > 3) { 
      const msg = 'Only 3 images can be uploaded at a time'
      e.target.value = null // discard selected file
      console.log(msg)
      return false;
    }
    return true; 
  }

  function handleSubmit(){
    setFile({
      name:name.current.value.trim(),
      comment:comment.current.value.trim(),
      file:file.current.value.trim()
    })
    const data = new FormData(); 
    data.append('file', selectedFile);
    axios.post("http://localhost:8000/upload", data, { 
      // receive two    parameter endpoint url ,form data
    }).then(res => { // then print response status
      console.log(res.statusText);
    })
    console.log("submitted");
  };


  function onChangeHandler(e){
    // setFile(e.target.files[0])
    console.log(e.target.files[0])
    console.log(selectedFile)
  }

return(
<div>
      <form className="comment-form" encType="multipart/form-data" onSubmit={handleSubmit}>
        <label>name</label>
        <input
          type="text"
          id="name"
          name="name"
          ref={name}
          /* call the ref with the 'ref' attribute */ required
        ></input>
        <br />
        <label>comment</label>
        <textarea rows="2" id="comment" name="comment" ref={comment} required></textarea>
        <br />
        <div>
        <label htmlFor="imgUpload">upload your website image here <i className="fas fa-chevron-circle-up"></i></label>
        <input type="file" id="ref" ref={file} name="file" accept="image/png, image/jpeg" onChange={onChangeHandler}/>
        </div>
        <br />
        <button type="submit">
          submit
        </button>
        <br />
      </form>
    </div>
    )
}

export default Game;
