import React,{useState,useEffect} from 'react';
import uuid from "uuid/v4";
import axios from 'axios';

const Input=()=>{
  const nameInput=React.createRef();
  const commentInput=React.createRef();
  const fileInput=React.createRef(); 

  const [response, setResponse]=useState('')
  const [post, addNewPost]=useState('')
  const [responseToPost, addResponseToPost]=useState('')
  // const [call, setCall]=useState({
  //   response:'',
  //   post:'',
  //   responseToPost:''
  // })

useEffect(()=>{
    callApi().then(res=>setResponse({response:res.express})).catch(err=> console.log(err));
  },[])

async function callApi(){
const response= await fetch('/api/hello');
const body= await response.json();
console.log(body)
if(response.status !== 200) throw Error (body.message);
return body;
  }

function addPost(name, comment,file){
    const newPost = {
      // uuid: uuid(),
      name: name,
      comment: comment,
      date: new Date().toLocaleString(),
      file:file,
      showing: false
    }; 
    // setNewPost({post:{...newPost[uuid()]}});
    addNewPost(newPost[uuid()])
    console.log(newPost)
    console.log(call.response)
    console.log(call.post)
    console.log(call.responseToPost)
    console.log(call)
    console.log(post)
    return newPost
  };

  async function handleSubmit(e){
    e.preventDefault();
    const name = nameInput.current.value.trim();
    const comment = commentInput.current.value.trim();
    const file = fileInput.current.value.trim();
    addPost(name,comment,file)
    // const data = new FormData(e.target); 
    // setCall({post:data})
    const response=await fetch('/api/world',{
      method:'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify( post ),
    })
    const body=await response.text();
    console.log(body)
    setCall({responseToPost:body})
    console.log(call.response)
    console.log(call.post)
    console.log(call.responseToPost)
    // axios.post("/upload", data, { 
    // }).then(res => { 
    //   console.log(res.statusText);
    // })
    // console.log("submitted");
    // e.currentTarget.reset();
  };

  function onChangeHandler(e){
    // setFile(e.target.files[0])
    // console.log(selectedFile)
  }

return(
<div>
      <form className="comment-form" encType="multipart/form-data" onSubmit={handleSubmit}>
        <label>name</label>
        <input
          type="text"
          id="name"
          name="name"
          ref={nameInput}
          required
        ></input>
        <br/>
        <label>comment</label>
        <textarea rows="2" id="comment" name="comment" ref={commentInput} required></textarea>
        <br/>
        <div>
        <label htmlFor="imgUpload">upload your website image here <i className="fas fa-chevron-circle-up"></i></label>
        <input type="file" id="ref" ref={fileInput} name="file" accept="image/png, image/jpeg" onChange={onChangeHandler}/>
        </div>
        <br/>
        <button type="submit">
          submit
        </button>
        <br />
      </form>
<h1>{call.response}</h1>
    </div>
    )
}

export default Input;
